function rkg(funderyv)%%  	numerical integration by Runge-Kutta-Gill's method%global x_ dx_ qx_ nde_ dth_ t_a = [.5, .2928932188134525, 1.707106781186548, .1666666666666667];b = [2, 1, 1, 2];c = [.5, .2928932188134525, 1.707106781186548, .5];evalstr = [funderyv];eval(evalstr);for j = 1 : 4    aj = a(j); bj = b(j); cj = c(j);        r1  = dth_ .* dx_;    r2  = aj .* (r1 - bj.* qx_);    x_  = x_ + r2;    qx_ = qx_ + 3.0 .* r2 - cj .* r1;        if j == 1 || j == 3        t_ = t_ + dth_ / 2.0;    end    if j <= 3        eval(evalstr);    end    end