%%	calculate rigid body motion %	clear all    close all    	const; % global variables	dth_ = 0.01;        % time step	nde_ = 2;          % the number of state variables	smt_ = 10;          % end time		x_  = zeros(nde_,1);    dx_ = zeros(nde_,1);    qx_ = zeros(nde_,1);%%	simulation%	    	initial;	% initial condition    	t_ = 0.0;	ts = [];    %空行列     xs = [];    tic    while(1) % Runge-Kutta-Gill method		rkg('motion');% ルンゲクッタ法でmotionファイルの運動方程式を解く        			xs = [xs, x_];            ts = [ts; t_];				disp(['time = ',num2str(t_)]);		if t_ >= smt_%シミュレーション時間が終わったら			break%強制終了		end    end    xs = xs'; % Transpose    simplot;%グラフを作成    toc